// widgets.js

// Actions
const CREATE = "todo/CREATE";
const UPDATE = "todo/UPDATE";
const DELETE = "todo/DELETE";

const initialState = {
  todos: [
    {
      id: 1,
      title: "리액트 공부하기",
      body: "리액트 기초를 공부해봅시다.",
      isDone: false,
    },
  ],
};

// Action Creators

export function createTodo(todo) {
  return { type: CREATE, todo: todo };
} //이거 자체가 액션객체가 됨

export function toggleStatusTodo(todo) {
  return { type: UPDATE, todo };
}

export function deleteTodo(todo) {
  return { type: DELETE, todo };
}

//Reducer
export default function reducer(state = initialState, action = {}) {
  switch (action.type) {
    case "todo/CREATE":
      const newTodo = [...state.todos, action.todo];
      return {
        ...state,
        todos: newTodo,
      };
    case "todo/UPDATE":
      return {
        ...state,
        todos: state.todos.map((todo) => {
          if (todo.id === action.todo) {
            return {
              ...todo,
              isDone: !todo.isDone,
            };
          } else {
            return todo;
          }
        }),
      };
    case "todo/DELETE":
      return {
        ...state,
        todos: state.todos.filter((todo) => todo.id !== action.todo),
      };
    default:
      return state;
  }
}
