import axios from "axios";
import React, { useEffect, useState } from "react";

function App() {

  const [inputs, setInputs] = useState({
    nick: "",
    title: "",
    body: "",
  });

  //api
  useEffect(() => {
    createUsers();
    // deleteUsers();
  }, []);


  /*create users*/
  const createUsers = () => {
    return axios.post("http://localhost:3002/todo", {
      nick: inputs.nick,
      title: inputs.title,
      body: inputs.body,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const id = e.target.id.value;
    const nick = e.target.nick.value;
    const title = e.target.title.value;
    const body = e.target.body.value;
    console.log(`nick ${nick}, title ${title},body ${body}`);
  };
  const handleOnChange = (e) => {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });
    console.log(inputs.id);
  };

  return (

      <div
        onSubmit={handleSubmit}
        onChange={handleOnChange}
        className="ml-4 flex flex-col w-2/12"
      >
        <label>nick</label>
        <input className="border" type="text" name="nick" />
        <label>title</label>
        <input className="border" type="text" name="title" />

        <label>body</label>
        <input className="border" type="text" name="body" />


        <button className="bg-red-100 p-3 mt-4" onClick={createUsers}>
          Submit
        </button>

      </div>
  );
  }

export default App;
