import axios from "axios";
import React, { useEffect, useState } from "react";

export default function App() {
  const [login, setLogin] = useState([]);
  const [inputs, setInputs] = useState({
    nick: "",
    title: "",
    body: "",
  });

  //api
  useEffect(() => {
    createUsers();
    // deleteUsers();
  }, []);

  /*get users*/
  const getUsers = () => {
    return axios.get("http://localhost:3006/users").then((res) => {
      setLogin(res.data);
      console.log(res.data);
    });
  };
  /*create users*/
  const createUsers = () => {
    return axios.post("http://localhost:3002/todo", {
      nick: inputs.nick,
      title: inputs.title,
      body: inputs.body,
    });
  };
  /*일부업데이트*/
  const updateUsers = () => {
    return axios.patch("http://localhost:3006/users/2", {
      firstName: "수정된이름",
    });
  };
  /*id 새로 생성 업데이트*/
  const newupdateUsers = () => {
    return axios.put("http://localhost:3006/users/2", {
      reset: "this is reset",
    });
  };
  /*users delete*/
  const deleteUsers = () => {
    return axios.delete(`http://localhost:3006/users/1`);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const id = e.target.id.value;
    const name = e.target.name.value;
    const title = e.target.lastname.value;
    const lastname = e.target.lastname.value;
    console.log(`name ${name}, lastname ${lastname},id ${id}`);
  };
  const handleOnChange = (e) => {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });
    console.log(inputs.id);
  };
  return (
    <>
      <form
        onSubmit={handleSubmit}
        onChange={handleOnChange}
        className="ml-4 flex flex-col w-2/12"
      >
        <label>nick</label>
        <input className="border" type="text" name="nick" />
        <label>title</label>
        <input className="border" type="text" name="title" />

        <label>body</label>
        <input className="border" type="text" name="body" />


        <button className="bg-red-100 p-3 mt-4" onClick={createUsers}>
          Submit
        </button>

      </form>
    </>
  );
}
