import axios from "axios";
import React, {useState} from "react";
import "./style.css";


function Form() {

  const [inputs, setInputs] = useState({
    nick: "",
    title: "",
    body: "",
  });


  /*create users*/
  const createUsers = () => {
    return axios.post("http://localhost:3002/todo", {
      title: inputs.title,
      nick: inputs.nick,
      body: inputs.body,
    }
    );
  };return alert(저장되었습니다);

  const handleSubmit = (e) => {
    e.preventDefault();
    const nick = e.target.nick.value;
    const title = e.target.title.value;
    const body = e.target.body.value;
    console.log(`title ${title}, body ${body},nick ${nick}`);
  };
  const handleOnChange = (e) => {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });
  };
  return (
    <>
      <form
        onSubmit={handleSubmit}
        onChange={handleOnChange}
        className="ml-4 flex flex-col w-2/12 form_container"
      >
        <label className="form-label">닉네임</label>
        <input className="form-control form-control-lg" type="text" name="nick" />
        <label className="form-label">아이디</label>
        <input className="form-control form-control-lg" type="text" name="title" />
        <label className="form-label">내용</label>
        <input className="form-control form-control-lg" type="text" name="body" />     
        <button className="inputButton mb-2" onClick={createUsers}>
          저장
        </button>
      </form>
    </>
  );
}
export default Form;
