import axios from "axios";
import React, { useEffect, useState } from "react";

function Form() {
  const initialState = {
    id: 0,
    title: "",
    nick: "",
    body: "",
    isDone: false,
  };

  axios({
    url: 'http://localhost:3001/todo', // 통신할 웹문서
    method: 'post', // 통신할 방식
    data: { // 인자로 보낼 데이터
      foo: 'tod'
    }
  });
  const [todo, setTodo] = useState(initialState);
  //api
  useEffect(() => {
    createTodos();
  }, []);

  /*create users*/
  const createTodos = () => {
    return axios.post("http://localhost:3001/todo", {
      nick: todo.nick,
      title: todo.title,
      body: todo.body,
    });
  };

  const handleSubmit = (e) => {
    e.createTodos();
    const nick = e.target.nick.value;
    const title = e.target.title.value;
    const body = e.target.body.value;
    console.log(`nick ${nick}, title ${title}, body ${body}`);
  };

  return (
    <>
      <form
        onSubmit={handleSubmit}

      >
        <label>닉네임</label>
        <input type="text" name="nick" />
        <label>제목</label>
        <input  type="text" name="title" />
        <label>내용</label>
        <input  type="text" name="body" />
        <button onClick={createTodos}>
          저장
        </button>

      </form>
    </>
  );
}
export default Form;
