import axios from "axios";
import React, { useEffect, useState } from "react";

export default function App() {

  const [inputs, setInputs] = useState({
    nick: "",
    name: "",
    body: "",
  });


  /*create users*/
  const createUsers = () => {
    return axios.post("http://localhost:3002/todo", {
      title: inputs.title,
      nick: inputs.nick,
      body: inputs.body,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const nick = e.target.nick.value;
    const name = e.target.name.value;
    const body = e.target.body.value;
    console.log(`name ${name}, lastname ${body},nick ${nick}`);
  };
  const handleOnChange = (e) => {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });
    console.log(inputs.id);
  };
  return (
    <>
      <form
        onSubmit={handleSubmit}
        onChange={handleOnChange}
        className="ml-4 flex flex-col w-2/12"
      >
        <label>닉네입</label>
        <input className="border" type="text" name="nick" />
        <label>아이디</label>
        <input className="border" type="text" name="name" />
        <label>내용</label>
        <input className="border" type="text" name="body" />     
        <button onClick={createUsers}>
          Submit
        </button>
      </form>
    </>
  );
}
