import axios from "axios";
import React, { useEffect, useState } from "react";

export default function App() {

  const initialState = {
    id: 0,
    title: "",
    nick: "",
    body: "",
    isDone: false,
  };
  const [todo, setTodo] = useState(initialState);
  const [inputs, setInputs] = useState({
    name: "",
    id: "",
  });

  //api
  useEffect(() => {
    createUsers();
    // deleteUsers();
  }, []);

  /*get users*/
  const getUsers = () => {
    return axios.get("http://localhost:3002/users").then((res) => {
      setLogin(res.data);
      console.log(res.data);
    });
  };
  /*create users*/
  const createUsers = () => {
    return axios.post("http://localhost:3002/users", {
      name: inputs.name,
      id: inputs.id,
    });
  };

  /*id 새로 생성 업데이트*/
  const newupdateUsers = () => {
    return axios.put("http://localhost:3002/users/2", {
      reset: "this is reset",
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const nick = e.target.nick.value;
    const title = e.target.title.value;
    const body = e.target.body.value;
    console.log(`title ${title}, body ${body},nick ${nick}`);
  };

  return (
    <>
      <form
        onSubmit={handleSubmit}
        className="ml-4 flex flex-col w-2/12"
      >
        <label>닉네임</label>
        <input className="border" type="text" name="nick" />
        <label>제목</label>
        <input className="border" type="text" name="title" />
        <label>내용</label>
        <input className="border" type="text" name="body" />
        <button className="bg-red-100 p-3 mt-4" onClick={createUsers}>
          저장
        </button>

      </form>
    </>
  );
}
